var mouseX, mouseY, arcSpaceship;
var scene = new THREE.Scene();
var camera = new THREE.PerspectiveCamera(75, window.innerWidth/window.innerHeight, 0.1, 1000);

var renderer = new THREE.WebGLRenderer();
renderer.setClearColor( 0x222222, 0 ); // background

renderer.setSize(window.innerWidth, window.innerHeight);
document.body.appendChild(renderer.domElement);


// Mesh
var group = new THREE.Group();
var loader = new THREE.JSONLoader();

loader.load('javascripts/arc170.json', function (geometry, materials) {

  var faceMaterial = new THREE.MeshFaceMaterial( materials );

  arcSpaceship = new THREE.Mesh(
    geometry,
    faceMaterial
  );

  arcSpaceship.receiveShadow = true;
  arcSpaceship.castShadow = true;

  // Grouping so I can play around later
  group.add(arcSpaceship);

  arcSpaceship.rotation.x += 1.005;

});

scene.add(group);
// Lights
var light = new THREE.AmbientLight( 0x404040 ); // soft white light
scene.add( light );

var directionalLight = new THREE.DirectionalLight( 0xeeeeee, 1 );
directionalLight.position.set( 1, 1, 1 );
scene.add( directionalLight );
camera.position.z = 1;


// Particle
// Create particle group
var particleGroup = new SPE.Group({
    texture: THREE.ImageUtils.loadTexture("data:image/png;base64,"),
    maxAge: 5,
    hasPerspective: 1,
    colorize: 1,
    transparent: .5,
    alphaTest: 0.5,
    depthWrite: false,
    depthTest: true,
    blending: THREE.AdditiveBlending
});

// Create particle emitter 0
var Untitled1Emitter = new SPE.Emitter( {
    type: 'cube',
    particleCount: 100,
    position: new THREE.Vector3( 0, 0, 0 ),
    positionSpread: new THREE.Vector3( 0, 0, 0 ),
    acceleration: new THREE.Vector3( 0.7, 0, 0 ),
    accelerationSpread: new THREE.Vector3( 0, 0, 0 ),
    velocity: new THREE.Vector3( 0, 0, 0 ),
    velocitySpread: new THREE.Vector3( 0, 0.1, 0.1 ),
    sizeStart: 1,
    sizeStartSpread: 0,
    sizeMiddle: 1,
    sizeMiddleSpread: 0,
    sizeEnd: 1,
    sizeEndSpread: 0,
    angleStart: 0,
    angleStartSpread: 0,
    angleMiddle: 0,
    angleMiddleSpread: 0,
    angleEnd: 0,
    angleEndSpread: 0,
    angleAlignVelocity: false,
    colorStart: new THREE.Color( 0xbe5200 ),
    colorStartSpread: new THREE.Vector3( 0, 0, 0 ),
    colorMiddle: new THREE.Color( 0x2c0600 ),
    colorMiddleSpread: new THREE.Vector3( 0, 0, 0 ),
    colorEnd: new THREE.Color( 0xffffff ),
    colorEndSpread: new THREE.Vector3( 0, 0, 0 ),
    opacityStart: 1,
    opacityStartSpread: 0,
    opacityMiddle: 0.5,
    opacityMiddleSpread: 0,
    opacityEnd: 0,
    opacityEndSpread: 0,
    duration: null,
    alive: 1,
    isStatic: 0
} );


particleGroup.addEmitter( Untitled1Emitter );

// Add mesh to your scene. Adjust as necessary.
scene.add( particleGroup.mesh );
particleGroup.mesh.position.x = 2;



var time = new THREE.Clock();

var render = function () {  
  //group.rotation.x += 0.005;
  //group.rotation.y += 0.005;
  //group.rotation.z += 0.005;

  
  camera.position.x = mouseX * 0.005;
  camera.position.y = -mouseY * 0.005;
  camera.lookAt(group.position);

  renderer.render(scene, camera);
  requestAnimationFrame(render);  
};

render();


// Mouse and resize events
document.addEventListener( 'mousemove', onDocumentMouseMove, false );
window.addEventListener('resize', onWindowResize, false);

function onWindowResize() {
  camera.aspect = window.innerWidth / window.innerHeight;
  camera.updateProjectionMatrix();
  renderer.setSize(window.innerWidth, window.innerHeight);
}

function onDocumentMouseMove( event ) {
  mouseX = event.clientX - window.innerWidth/2;
  mouseY = event.clientY - window.innerHeight/2;
}